           var format = 'CxSLSxx';
           var vals = packValueMask('CreateWindow', values);
           var packetLength = 3 + (values ? vals[2].length : 0);
           var args = [2, packetLength, wid, vals[1]];
           var valArr = vals[2];
           format += vals[0];
           args = args.concat(valArr);
           return [format, args];
