       function(time) {
           return [ 'CxSL', [ 32, 2, time ] ];
       }
   ],

   GrabKey: [
       function(wid, ownerEvents, modifiers, key, pointerMode, keybMode) {
           return [ 'CCSLSCCCxxx', [ 33, ownerEvents, 4, wid, modifiers, key, pointerMode, keybMode ] ];
       }
